import { React, useRef } from "react";
import { useNavigate } from "react-router-dom";
import "../App.css";
import Nav from "./Navbar";
import { Auth } from "../firebase-config";
import { useState, useEffect } from "react";
import { onAuthStateChanged, signOut } from "firebase/auth";

function Home() {
  const [user, setUser] = useState({});
  const [tweet, setTweet] = useState({});
  const navigate = useNavigate();
  const tweetInputRef = useRef();

  useEffect(() => {
    onAuthStateChanged(Auth, (currentUser) => {
      setUser(currentUser);
    });
  }, []);

  function submitHandler(e) {
    e.preventDefault();

    const setTweet = tweetInputRef.current.value;

    const meetingData = {
      tweet: setTweet,
    };

    //Post Tweets
    fetch(
      "https://fir-auth-5ce51-default-rtdb.firebaseio.com/tweets.json",
      {
        method: "POST",
        body: JSON.stringify(meetingData),
      },
      alert("success")
    ).then(() => {
      navigate("/home", { replace: true });
    });
  }

  return (
    <div>
      <Nav />

      <div className="container text-center mt-1">
            
                <form className='meeting-form' onSubmit={submitHandler}>
                <h1 className='title mb-3'>Create New Meetings</h1>
                    <div className='form-group'>
                        <label htmlFor="exampleInputEmail1">Meeting Title</label>
                        <input type="text" className="form-control" id="title" placeholder="Enter the meeting title" />
                    </div>
                    <div className='form-group'>
                        <label htmlFor="exampleInputEmail1">Image keyword</label>
                        <input type="text" className="form-control" id="title" placeholder="Enter the meeting title" />
                    </div>
                    <div className='form-group'>
                        <label htmlFor="exampleInputEmail1">Meeting Date</label>
                        <input type="date" className="form-control" id="title" placeholder="Enter the meeting title" />
                    </div>
                    <div className='form-group'>
                        <label htmlFor="exampleInputEmail1">Meeting Title</label>
                        <textarea type="text" rows="5" className="form-control" id="title" placeholder="Enter the meeting title" />
                    </div>
                    <div className='form-group'>
                    <input type="submit" className="btn" value="Add Meeting"/>
                    </div>

                </form>

            </div>

    </div>
  );
}
export default Home;
