 fetch('https://fir-auth-5ce51-default-rtdb.firebaseio.com/tweets.json')
 .then(response => response.json()).then(data => {
   const tweet = [];
   for (const key in data) {
    const value = {id: key,
    ...data[key]
 }
    tweet.push(value);
}
 setTweet(tweet);
 console.log(tweet);
 });
},[]);

  return (
    <div>
      <Nav />

      <div className=" flex flex-col lg:flex-row text-sm dark:bg-black dark:text-white">
        <div className="w-full lg:w-1/4 pl-4 lg:pl-0 pr-6 mt-8 mb-4 dark:bg-black dark:text-white"></div>
        <div className="w-full lg:w-1/2 bg-white mb-4 dark:bg-black dark:text-white">
          
          
            <div className="flex mb-42 mb-12">
              <div>
                <h1 className="px-0 py-2 text-2xl font-bold text-white">
                  Hello {user.email}
                </h1>

                <h1 className="text-lg">
                 Here are your tweets &#129303;
                </h1>
              </div>
            </div>
          

            <div className={loading ? "loading" : "d-none"}>
                <div className='loading-animation'>loading...</div>
            </div>
            <div className={meetings.length == 0 ? "container text-center" : "d-none"}>
                <p>No Tweets available!!</p>
            </div>

          <div>

          {
                        meetings.filter((meeting)=>{
                            let date = new Date();
                            let current = `${date.getDate()}/${date.getMonth()+1}/${date.getFullYear()}`;
                            let today = moment(current);
                            let meetingDate = moment(meeting.date);
                            console.log(today)
                            console.log(meetingDate)

                            if(meetingDate.isSameOrAfter(today)){
                                return meeting;
                            }
                        }).map(meeting => {
                            let date = moment(meeting.date).format(' MMMM Do YYYY');
                           return <Tweets title={meeting.title} desc={meeting.desc} date={date} img={meeting.img} />
                        })
                    }

          </div>
          
        </div>
      </div>
    </div>
  );
}
export default Home;
