import { Link } from 'react-router-dom';
import './App.css';
import './index.css';

import { useState, useEffect } from "react";
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  onAuthStateChanged,
  signOut,
} from "firebase/auth";
import { auth } from "./firebase-config";

function Login() {
  const [registerEmail, setRegisterEmail] = useState("");
  const [registerPassword, setRegisterPassword] = useState("");
  const [loginEmail, setLoginEmail] = useState("");
  const [loginPassword, setLoginPassword] = useState("");

  const [user, setUser] = useState({});

  // onAuthStateChanged(auth, (currentUser) => {
  //   setUser(currentUser);
  // });
  useEffect(() => {
    onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });
  }, []);
  const register = async () => {
    try {
      const user = await createUserWithEmailAndPassword(
        auth,
        registerEmail,
        registerPassword
      );
      console.log(user);
    } catch (error) {
      alert(error.message);
    }
  };

  const login = async () => {
    try {
      const user = await signInWithEmailAndPassword(
        auth,
        loginEmail,
        loginPassword
      );
      console.log(user);
    } catch (error) {
      alert(error.message);
    }
  };

  const logout = async () => {
    await signOut(auth);
  };


  return (
    <div>    
<section className="flex justify-center items-center h-screen bg-gray-100 dark:bg-black ">
    <div className="border-2 max-w-md w-full bg-white rounded p-6 space-y-4 dark:bg-gray-900 dark:text-white">
        <div className="mb-4 text-center">
            <p className="text-gray-600">WELCOME BACK</p>
            <h2 className="text-lg font-bold">Log into your account</h2>
        </div>
        <form class="mt-10 ">
        <div>
        <label class="block mb-1" for="email">Email or Username</label>
            <input required className="dark:bg-gray-900 dark:text-white w-full p-4 text-sm bg-gray-50 focus:outline-none border border-gray-200 rounded text-gray-600" type="text" placeholder="Enter your email or username" required ></input>
        </div>
        <div>
          <div className='flex justify-between mt-3'>
        <label class="block mb-1 " for="email">Password</label>

            <a href="/" className="text-sm text-blue-600 hover:underline ">Forgot password?</a>
           
            </div>
            <input type= 'password' className="dark:bg-gray-900 dark:text-white w-full p-4 text-sm bg-gray-50 focus:outline-none border border-gray-200 rounded text-gray-600"   placeholder="Enter your password" required />        

        </div>
        <div className='mt-3'>
            <Link to="/home">
            <button className="w-full py-4 bg-blue-600 hover:bg-blue-700 rounded text-sm font-bold text-gray-50 transition duration-100" required>Login</button>
            </Link>
        </div>
        <div className="flex items-center justify-between mt-2">
            
          
               <p className='text-gray-600'>Not registered yet? <a href="/" className='text-white' >Register &#129062;</a></p>
            
        </div>
        </form>
    </div>
</section>

    </div>
  );
}

export default App;
