import { React, useRef } from "react";
import { useNavigate } from "react-router-dom";
import "../App.css";
import Nav from "./Navbar";
import { Auth } from "../firebase-config";
import { useState, useEffect } from "react";
import { onAuthStateChanged, signOut } from "firebase/auth";

function Home() {
  const [user, setUser] = useState({});
  const [tweet, setTweet] = useState({});
  const navigate = useNavigate();
  const tweetInputRef = useRef();

  useEffect(() => {
    onAuthStateChanged(Auth, (currentUser) => {
      setUser(currentUser);
    });
  }, []);

  function submitHandler(e) {
    e.preventDefault();

    const setTweet = tweetInputRef.current.value;

    const meetingData = {
      tweet: setTweet,
    };

    //Post Tweets
    fetch(
      "https://fir-auth-5ce51-default-rtdb.firebaseio.com/tweets.json",
      {
        method: "POST",
        body: JSON.stringify(meetingData),
      },
      alert("success")
    ).then(() => {
      navigate("/home", { replace: true });
    });
  }

  return (
    <div>
      <Nav />

      <div className=" flex flex-col lg:flex-row text-sm dark:bg-black dark:text-white">
          <div className="flex mb-42 mb-12">
              <div className="">
                <h1 className="px-0 py-2 text-2xl font-bold text-white">
                  Hello {user.email}
                </h1>

                <h3>
                  How are you doing today? Would you like to share something
                  with the <br />
                  community &#129303;
                </h3>
              </div>
            </div>
            <form onSubmit={submitHandler}>
              <div className="dark:bg-zinc-800 rounded">
                {/* <!--middle creat tweet--> */}
                <h2 className="px-4 py-2 text-lg font-semibold text-white">
                  Create Post
                </h2>

                <div className="flex dark:bg-zinc-600 rounded mx-4">
                  <div className="m-2 w-10 py-1">
                    <img
                      className="inline-block h-10 w-10 rounded-full"
                      src="https://pbs.twimg.com/profile_images/1121328878142853120/e-rpjoJi_bigger.png"
                      alt=""
                    />
                  </div>
                  <div className="flex-1 px-2 pt-2 mt-2">
                    <textarea
                      className=" bg-transparent text-gray-400 font-medium text-lg w-full"
                      rows="2"
                      cols="50"
                      placeholder="What's happening?"
                      ref={tweetInputRef}
                    ></textarea>
                  </div>
                </div>
                {/* <!--middle creat tweet below icons--> */}
                <div className="flex">
                  <div className="flex-1 mb-3">
                    <button
                      type="submit"
                      className="bg-blue-400 hover:bg-blue-500 mt-5 text-white font-bold py-2 px-8 rounded-full mr-8 float-right"
                    >
                      Post
                    </button>
                  </div>
                </div>
              </div>
            </form>
      </div>
    </div>
  );
}
export default Home;
