import {useNavigate} from 'react-router-dom';
import { Alert } from "react-bootstrap"
import './App.css';
import './index.css';

import { useState, useEffect } from "react";
import {
  createUserWithEmailAndPassword,
  onAuthStateChanged,
  signOut,
} from "firebase/auth";
import { auth } from "./firebase-config";

function App() {
  const [registerEmail, setRegisterEmail] = useState("");
  const [registerPassword, setRegisterPassword] = useState("");
  const [error, setError] = useState("")
  const navigate = useNavigate();

  const [user, setUser] = useState({});

  useEffect(() => {
    onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      
    });
  }, []);

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      setError("")
      const user = await createUserWithEmailAndPassword(
        auth,
        registerEmail,
        registerPassword
      );
      
     navigate('/home')
    } catch {
      setError("Failed to create an account")
    }
  };


  return (
    <div>    
<section className="flex justify-center items-center h-screen bg-gray-100 dark:bg-black ">
    <div className="border-2 max-w-md w-full bg-white rounded p-6 space-y-4 dark:bg-gray-900 dark:text-white">
    <h1 className="text-white text-center">WELCOME</h1>
        <div className="mb-4 text-center">
            
            <h2 className="text-lg font-bold">Sign Up</h2>
            {error && <Alert variant="danger">{error}</Alert>}
        </div>
        <form class="mt-10 ">
        <div>
        <label class="block mb-1" >Email</label>
            <input className="dark:bg-gray-900 dark:text-white w-full p-4 text-sm bg-gray-50 focus:outline-none border border-gray-200 rounded text-gray-600" type="text" placeholder="Enter your email or username" required 
            onChange={(event) => {
                setRegisterEmail(event.target.value);
              }}/>
        </div>
        <div>
          <div className='flex justify-between mt-3'>
        <label class="block mb-1 ">Password</label>
           
            </div>
            <input type= 'password' className="dark:bg-gray-900 dark:text-white w-full p-4 text-sm bg-gray-50 focus:outline-none border border-gray-200 rounded text-gray-600"   placeholder="Enter your password" required 
            onChange={(event) => {
                setRegisterPassword(event.target.value);
              }}/>        

        </div>
        <div className='mt-3'>
            <button type="submit" className="w-full py-4 bg-blue-600 hover:bg-blue-700 rounded text-sm font-bold text-gray-50 transition duration-100"  >Register</button>
        </div>
        <div className="flex items-center justify-between mt-2">
               <p className='text-gray-600'>Already have a account? <a href="/" className='text-white' >Login &#129062;</a></p>
        </div>
       
        </form>
    </div>
</section>

    </div>
  );
}

export default App;
