import { useNavigate } from "react-router-dom";
import "./App.css";
import "./index.css";

import { useState, useEffect } from "react";
import {
  createUserWithEmailAndPassword,
  onAuthStateChanged,
} from "firebase/auth";
import { Auth } from "./firebase-config";

function App() {
  const [registerEmail, setRegisterEmail] = useState("");
  const [registerPassword, setRegisterPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const [user, setUser] = useState({});
  const [userName, setUserName] = useState({});

 

  useEffect(() => {
    onAuthStateChanged(Auth, (currentUser) => {
      setUser(currentUser);
    });
  }, []);


  async function handleSubmit(e) {
    reset();
    e.preventDefault();
    try {
      await createUserWithEmailAndPassword(
        Auth,
        registerEmail,
        registerPassword,
        userName
      );

      navigate("/home");
    } catch (error) {
      setError(error.message);
      setUserName("");
      setRegisterEmail("");
      setRegisterPassword("");

    }
  }

  return (
    <div>
      <section className="flex justify-center items-center h-screen bg-gray-100 dark:bg-black ">
        <div className="border-2 max-w-md w-full bg-white rounded p-6 space-y-4 dark:bg-gray-900 dark:text-white">
          {error && (
            <div className="flex justify-center mt-2">
              <div className="flex justify-between w-full px-4 py-2 text-red-600 bg-red-100 rounded">
                <p>{error}</p>
                <span>
                  <svg
                    class="fill-current h-6 w-6 text-red-500"
                    role="button"
                    xmlns="http://www.w3.org/2000/svg"
                    viewBox="0 0 20 20"
                  >
                    <title>Close</title>
                    <path d="M14.348 14.849a1.2 1.2 0 0 1-1.697 0L10 11.819l-2.651 3.029a1.2 1.2 0 1 1-1.697-1.697l2.758-3.15-2.759-3.152a1.2 1.2 0 1 1 1.697-1.697L10 8.183l2.651-3.031a1.2 1.2 0 1 1 1.697 1.697l-2.758 3.152 2.758 3.15a1.2 1.2 0 0 1 0 1.698z" />
                  </svg>
                </span>
              </div>
            </div>
          )}

          <h1 className="text-white text-center">WELCOME</h1>

          <div className="mb-4 text-center">
            <h2 className="text-lg font-bold">Sign Up</h2>
          </div>
          <form onSubmit={handleSubmit} className="mt-10 ">
            <div>
              <label className="block mb-1">Username</label>
              <input
                className="dark:bg-gray-900 dark:text-white w-full p-4 text-sm bg-gray-50 focus:outline-none border border-gray-200 rounded text-gray-600"
                type="text"
                placeholder="Enter your username"
                required
                onChange={(event) => {
                  setUserName(event.target.value);
                }}
              />
            </div>
            <div className="mt-3">
              <label className="block mb-1">Email</label>
              <input
                className="dark:bg-gray-900 dark:text-white w-full p-4 text-sm bg-gray-50 focus:outline-none border border-gray-200 rounded text-gray-600"
                type="text"
                placeholder="Enter your email "
                required
                onChange={(event) => {
                  setRegisterEmail(event.target.value);
                }}
              />
            </div>
            <div>
              <div className="flex justify-between mt-3">
                <label className="block mb-1 ">Password</label>
              </div>
              <input
                type="password"
                className="dark:bg-gray-900 dark:text-white w-full p-4 text-sm bg-gray-50 focus:outline-none border border-gray-200 rounded text-gray-600"
                placeholder="Enter your password"
                required
                onChange={(event) => {
                  setRegisterPassword(event.target.value);
                }}
              />
            </div>
            <div className="mt-3">
              <button
                type="submit"
                className="w-full py-4 bg-blue-600 hover:bg-blue-700 rounded text-sm font-bold text-gray-50 transition duration-100"
              >
                Register
              </button>
            </div>
            <div className="flex items-center justify-between mt-2">
              <p className="text-gray-600">
                Already have a account?{" "}
                <a href="/" className="text-white">
                  Login &#129062;
                </a>
              </p>
            </div>
          </form>
        </div>
      </section>
    </div>
  );
}

export default App;
