import { Link, useNavigate } from "react-router-dom";
import "./App.css";
import "./index.css";
import { useState, useEffect } from "react";
import { signInWithEmailAndPassword, onAuthStateChanged } from "firebase/auth";
import { Auth } from "./firebase-config";

function App() {
  const [loginEmail, setLoginEmail] = useState("");
  const [loginPassword, setLoginPassword] = useState("");
  const [error, setError] = useState("");
  const [user, setUser] = useState({});
  const navigate = useNavigate();


  useEffect(() => {
    onAuthStateChanged(Auth, (currentUser) => {
      setUser(currentUser);
    });
  }, []);

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      await signInWithEmailAndPassword(Auth, loginEmail, loginPassword);

      navigate("/home");
    } catch (error) {
      setError(error.message);
    };
  }

  return (
    <div>
        <section className="flex justify-center items-center h-screen bg-gray-100 dark:bg-black ">
       

          <div className="border-2 max-w-md w-full bg-white rounded p-6 space-y-4 dark:bg-gray-900 dark:text-white">
          {error && <div className="flex justify-center mt-2">
    <div className="flex justify-between w-full px-4 py-2 text-red-600 bg-red-100 rounded">
        <p>{error}</p><span></span>
    </div>
</div>}
            <div className="mb-4 text-center">
              <p className="text-gray-600">WELCOME BACK</p>
              
              <h2 className="text-lg font-bold">Log into your account</h2>
              
            </div>
            <form onSubmit={handleSubmit} className="mt-10 ">
              <div>
                <label className="block mb-1">Email or Username</label>
                <input
                  className="dark:bg-gray-900 dark:text-white w-full p-4 text-sm bg-gray-50 focus:outline-none border border-gray-200 rounded text-gray-600"
                  type="text"
                  placeholder="Enter your email or username"
                  required
                  onChange={(event) => {
                    setLoginEmail(event.target.value);
                  }}
                />
              </div>
              <div>
                <div className="flex justify-between mt-3">
                  <label className="block mb-1 ">Password</label>

                  <a
                    href="/"
                    className="text-sm text-blue-600 hover:underline "
                  >
                    Forgot password?
                  </a>
                </div>
                <input
                  type="password"
                  className="dark:bg-gray-900 dark:text-white w-full p-4 text-sm bg-gray-50 focus:outline-none border border-gray-200 rounded text-gray-600"
                  placeholder="Enter your password"
                  required
                  onChange={(event) => {
                    setLoginPassword(event.target.value);
                  }}
                />
              </div>
              <div className="mt-3">
                <button
                  type="submit"
                  className="w-full py-4 bg-blue-600 hover:bg-blue-700 rounded text-sm font-bold text-gray-50 transition duration-100"
                >
                  Login
                </button>
              </div>
              <div className="flex items-center justify-between mt-2">
                <p className="text-gray-600">
                  Not registered yet? &nbsp;
                  <a href="/register" className="text-white">
                    Register &#129062;
                  </a>
                </p>
              </div>
            </form>
          </div>
        </section>
     
    </div>
  );
}

export default App;
